import React, { Component } from 'react';
import { Link, withRouter} from 'react-router-dom';
import { textFieldGroup} from '../common/textFieldGroup';
import { TextAreaFieldGroup } from  '../common/TextAreaFieldGroup';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class AddExperience extends Component {
    constructor(props){
        super(props);
        this.state = {
            company: "",
            title:"",
            location:"",
            from:"",
            to :"",
            current:false,
            description: "",
            errors: {},
            disabled: ""

        }//end state
    }//end constructor
    render(){
        const { errors } = this.state;
        return(
            <div className="add-experience">
                 <div className="container">
                    <div className="row">
                        <div className="col-md-8 m-auto">
                            <Link to="/dashboard" className="btn btn-light">Go Back</Link>
                            <h1 className="display-4 text-center">Add Experience</h1>
                            <p className="lead text-center">Add some information</p>
                            <small className="d-block pb-3">required fields marked with an *</small>
                            <form onSubmit={this.onSubmit}>
                                    <TextFieldGroup 
                                        placeholder = "* Profile Handler"
                                        name="handle"
                                        onChange={this.onChange}
                                        value={this.state.handle}
                                        errors={errors.handle}
                                        info="Handle for your profile URL.Full name, company name, nickname, etc...(This info CANNOT be changed later)"
                                    />
                                    <SelectListGroup
                                        placeholder = "Status"
                                        name="status"
                                        onChange={this.onChange}
                                        value={this.state.status}
                                        errors={errors.status}
                                        info="Your current job status"
                                        options = {options}

                                    />
                                    <TextFieldGroup 
                                        placeholder = "Company"
                                        name="company"
                                        onChange={this.onChange}
                                        value={this.state.company}
                                        errors={errors.company}
                                        info="Company Information"
                                    />
                                    <TextFieldGroup 
                                        placeholder = "website"
                                        name="website"
                                        value={this.state.website}
                                        errors={errors.website}
                                        info="Website Information"
                                        onChange = {this.onChange}
                                    />
                                    <TextFieldGroup 
                                        placeholder = "Location"
                                        name="location"
                                        onChange={this.onChange}
                                        value={this.state.location}
                                        errors={errors.location}
                                        info="Location Information"
                                    />
                                    <TextFieldGroup 
                                        placeholder = "Skills"
                                        name="skills"
                                        errors={errors.skills}
                                        info="Skills Information"
                                        onChange = {this.onChange}
                                        value={this.state.skills}
                                    />
                                    <TextFieldGroup 
                                        placeholder = "Github Username"
                                        name="githubusername"
                                        value={this.state.githubusername}
                                        errors={errors.githubusername}
                                        info="Gituhub Information for repository inclusion"
                                        onChange = {this.onChange}
                                    />
                                    <TextAreaFieldGroup 
                                        placeholder = "Bio"
                                        name="bio"
                                        value={this.state.bio}
                                        errors={errors.bio}
                                        info="Bio Information"
                                        onChange = {this.onChange}
                                    />
                                    <div className="mb-3">
                                        <button type="button" className="btn btn-dark" onClick = {() => {
                                            this.setState(prevState => ({
                                                displaySocialNets: !prevState.displaySocialNets
                                                }));
                                            }}>
                                        </button>
                                        <span className="text-muted">  Click to add Social network sites ..Optional</span>
                                            {socialInputs}
                                        <input className="btn btn-info btn-block mt-4" type="submit" value="Submit"></input>
                                    </div>
                                </form>
                        </div>
                    </div>
                 </div>
            </div>
        )//end return
    }//end render
}

AddExperience.propTypes = {
    profile: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}//end default props

const mapStateToProps = (state) => ({
    profile: state.profile,
    errors: state.errors
})//end mapStateToProps

export default connect(mapStateToProps)(withRouter(AddExperience));
