import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import classnames from 'classnames';
import { loginUser } from '../../actions/authActions';


class Login extends Component{
    constructor(){
        super();
        this.state = {
            email:'',
            password:'',
            errors: {}
        };

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    };

    onChange(e){
        this.setState({
            [e.target.name]:e.target.value
        })//end onChange
    };//end onChange

    componentWillReceiveProps(nextProps){
        if(nextProps.auth.isAuthenticated){
            this.props.history.push('/dashboard');
        }//end if
        if(nextProps.errors){
              this.setState({
                errors: nextProps.errors})
        }//end if
    }

    onSubmit(e){
        e.preventDefault();
        const user = {
            email: this.state.email,
            password: this.state.password
        };//end newUser
        this.props.loginUser(user);
    };//end onSubmit

    render(){
        const { errors } = this.state;

        return(
        <div className='landing'>
            <div className="login">
                <div className="container">
                  <div className="row">
                    <div className="col-md-8 m-auto">
                        <h1 className="display-4 text-center">Log In</h1>
                        <p className="lead text-center">Sign in to your Devel'd-In account</p>
                        <form onSubmit={this.onSubmit} >
                            <div className="form-group">
                                <input type="email" className={classnames("form-control form-control-lg", { "is-invalid":errors.email })}
                                placeholder="Email Address" name="email" value={this.state.email} onChange={this.onChange} />
                                {errors.email &&(<div className="text-danger">{errors.email}</div>)}
                            </div>
                            <div className="form-group">
                                  <input type="password" className={classnames("form-control form-control-lg", {
                                    "is-invalid":errors.password })} placeholder="Password" name="password" value={this.state.password} onChange={this.onChange}/>
                                    {errors.password &&(<div className="text-danger">{errors.password}</div>)}
                            </div>
                                    <input type="submit" className="btn btn-info btn-block mt-4" />
                        </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        )//end return
    }//end render
}//end class Login
Login.propTypes = {
    loginUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    auth: state.auth,
    errors: state.errors   
});

export default connect(mapStateToProps,{loginUser})(Login);
