import axios from 'axios';
import { ADD_POST, GET_ERRORS } from './types'
//ADD POST
export const addPost = postData => dispatch => {
    axios
    .post('/api/posts', postData)
    .then(res => {
        dispatch({
            type: ADD_POST,
            payload: res.data
        })//end dispatch
    }).catch(err => dispatch({
        type: GET_ERRORS,
        payload: err.response.data
    }))//end catch
}//end addPost
