import React, { Component } from 'react';
import { Link, withRouter} from 'react-router-dom';
import  TextFieldGroup from '../common/textFieldGroup';
import  TextAreaFieldGroup  from '../common/TextAreaFieldGroup';
import  SelectListGroup  from  '../common/SelectListGroup';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

class addExperience extends Component {
    constructor(props){
        super(props);
        this.state = {
            company: "",
            title:"",
            location:"",
            from:"",
            to :"",
            current:false,
            description: "",
            errors: {},
            disabled: ""

        }//end state
    }//end constructor
    render(){
        const { errors } = this.state;

        return(
            <div className="add-experience">
                 <div className="container">
                    <div className="row">
                        <div className="col-md-8 m-auto">
                            <Link to="/dashboard" className="btn btn-light">Go Back</Link>
                            <h1 className="display-4 text-center">Add Experience</h1>
                            <p className="lead text-center">Add some information</p>
                            <small className="d-block pb-3">required fields marked with an *</small>
                            <form onSubmit={this.onSubmit}>

                                    <TextFieldGroup 
                                        placeholder = "Company"
                                        name="company"
                                        onChange={this.onChange}
                                        value={this.state.company}
                                        errors={errors.company}
                                        info="Company Information"
                                    />

                                    <TextFieldGroup 
                                        placeholder = "Location"
                                        name="location"
                                        onChange={this.onChange}
                                        value={this.state.location}
                                        errors={errors.location}
                                        info="Location Information"
                                    />
                                    <h3>From Date</h3>
                                    <TextFieldGroup 
                                        placeholder = "From Date"
                                        name="from"
                                        type="date"
                                        errors={errors.from}
                                        info="From date Information"
                                        onChange = {this.onChange}
                                        value={this.state.skills}
                                    />
                                    <h3>To Date</h3>
                                    <TextFieldGroup 
                                        placeholder = "To Date"
                                        name="to"
                                        type="date"
                                        value={this.state.to}
                                        errors={errors.to}
                                        info="To date information"
                                        onChange = {this.onChange}
                                        disabled = {this.state.disabled ? 'disabled': ''}
                                    />
                                    <div className="form-check mb-4">
                                        <input type="checkbox" 
                                        className="form-check-input"
                                        name="current"
                                        value= {this.state.current}
                                        checked ={this.state.current}
                                        onChange={this.onCheck}
                                        id="current"/>
                                        <label htmlFor="current" className="for-check-label">
                                        Current Position</label>
                                    </div>

                                    <TextAreaFieldGroup 
                                        placeholder = "Job Description"
                                        name="description"
                                        value={this.state.description}
                                        errors={errors.description}
                                        info="Description Information"
                                        onChange = {this.onChange}
                                    />
                                    <input type="submit" value="Submit" />

                            </form>
                        </div>
                    </div>
                 </div>
            </div>
        )//end return
    }//end render
}

addExperience.propTypes = {
    profile: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}//end default props

const mapStateToProps = (state) => ({
    profile: state.profile,
    errors: state.errors
})//end mapStateToProps

export default connect(mapStateToProps)(withRouter(addExperience));
