import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import classnames from 'classnames';
import { connect } from 'react-redux';
import { registerUser } from '../../actions/authActions';
import PropTypes from 'prop-types';

class Register extends Component{
    constructor(){
        super();
        this.state = {
            name: '',
            email:'',
            password:'',
            password2:'',
            errors: {}
        };//end this.state

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    };//end class Register

    componentWillReceiveProps(nextProps){
        if(nextProps.errors){
            this.setState({ errors: nextProps.errors })
        }
    }

    onChange(e){
        this.setState({
            [e.target.name]:e.target.value
        })//end onChange
    };//end onChange

    onSubmit(e){
        e.preventDefault();
        const newUser = {
            name: this.state.name,
            email: this.state.email,
            password: this.state.password,
            password2: this.state.password2
        };//end newUser
        this.props.registerUser(newUser);


    }//end onSubmit

    render(){
      const {errors} = this.state;

        return(
    <div className="register">
        <div className="container">
            <div className="row">
                <div className="col-md-8 m-auto">
                    <h1 className="display-4 text-center">Sign Up</h1>
                    <p className="lead text-center">Create your Devel'd-In account</p>
                        <form noValidate onSubmit={ this.onSubmit } >
                            <div className="form-group">
                                <input type="text" className={classnames("form-control form-control-lg", {
                                    "is-invalid":errors.name
                                })} placeholder="Name" name="name" value={this.state.name} onChange={this.onChange}  required />

                                    {errors.name && (<div className="text-danger">{errors.name}</div>)}

                            </div>
                            <div className="form-group">
                                <input type="email" className={classnames("form-control form-control-lg", {
                                    "is-invalid":errors.email
                                })} placeholder="Email Address" name="email" value={this.state.email} onChange={this.onChange} required/>
                                <small className="form-text text-muted">This site uses Gravatar so if you want a profile image, use a Gravatar email</small>
                                {errors.email &&(<div className="text-danger">{errors.email}</div>)}
                            </div>
                            <div className="form-group">
                                <input type="password" className={classnames("form-control form-control-lg", {
                                    "is-invalid":errors.password
                                })} placeholder="Password" name="password" value={this.state.password} onChange={this.onChange}/>
                                {errors.password &&(<div className="text-danger">{errors.password}</div>)}
                            </div>
                            <div className="form-group">
                                <input type="password" className={classnames("form-control form-control-lg", {
                                    "is-invalid":errors.password2
                                })} placeholder="Confirm Password" name="password2" value={this.state.password2} onChange={this.onChange}/>
                                {errors.password2 &&(<div className="text-danger">{errors.password2}</div>)}
                            </div>
                                <input type="submit" className="btn btn-info btn-block mt-4" />
                        </form>
                </div>
            </div>
        </div>
  </div>
        )//end return
    }//end render
}//end class Login

Register.propTypes = {
    registerUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
};
const mapStateToProps = (state) => ({
    auth: state.auth,
    errors: state.errors
});

export default connect(mapStateToProps,{registerUser})(Register);
