import React, {Component} from 'react';
import { connect } from 'react-redux';
import Proptypes from 'prop-types';
import TextFieldGroup from '../common/textFieldGroup';
import TextAreaFieldGroup from '../common/TextAreaFieldGroup';
import SelectListGroup from '../common/SelectListGroup';
import InputGroup from '../common/InputGroup';

class CreateProfile extends Component{
    constructor(props){
        super(props);
        this.state = {
            bio: '',
            company: '',
            errors: {},
            githubusername: '',
            handle: '',
            location: '',
            status: '',
            skillset: '',
            website: '',
            displaySocialNets: false,
            twitter: '',
            facebook: '',
            linkedin: '', 
            youtube: '',
            instagram: ''
        };
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

    }

    onSubmit(e){
        e.preventDefault();
        console.log('submit');
    }

    onChange(e){
        this.setState({[e.target.name]: e.target.value})
    }

    render(){
        const { errors } = this.state;
        return(
            <div className="create-profile">
                <div className="container">
                    <div className="row">
                        <div className="col-md-9 m-auto">
                            <h1 className="display-4 text-center">Create Your Profile</h1>
                                <p className="lead text-center">
                                    Porfile information
                                </p>
                                <small className="d-block" pb-3>Fields arked with * are required</small>

                                <form onSubmit={this.onSubmit}>
                                    <TextFieldGroup 
                                        placeholder = "Profile Handler"
                                        name="handle"
                                        value={this.state.handle}
                                        errors={errors.handle}
                                        info="Handle for your profile URL.Full name, company name, nickname, etc...(This info CANNOT be changed later)"
                                    />
                                </form>
                        </div>
                    </div>
                </div>
            </div>
        )//end return 
    }//end render
};//End create profile

CreateProfile.propTypes = {
    profile: Proptypes.object.isRequired,
    errors: Proptypes.object.isRequired
}

const mapStateToProps = state =>({
    profile: state.profile,
    errors: state.errors
})

export default connect(mapStateToProps)(CreateProfile);
