import { ADD_POST } from '../actions/types';

const initialState = {
    posts: [],
    post: {},
    loading: false,

}//end initial state

export default function(state= initialState, action){
    switch(action.type){
        case ADD_POST:
            return {
                ...state,
                posts:[action.payload, ...state.posts]
            }//end case ADD_POST

            default:
                return state;
    }//end switch
}//end anon function
