import React, { Component } from 'react';

class Login extends Component{
    constructor(){
        super();
        this.state = {
            name: '',
            email:'',
            password:'',
            password2:'',
            errors: {}
        };

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    };


    onChange(e){
        this.setState({
            [e.target.name]:e.target.value
        })//end onChange
    };//end onChange

    onSubmit(e){
        e.preventDefault();

        const newUser = {
            name: this.state.name,
            email: this.state.email,
            password: this.state.password,
            password2: this.state.password2
        };//end newUser
        console.log(newUser);  
    };//end onSubmit

    render(){
        return(
        <div className='landing'>
            <div className="login">
                <div className="container">
                  <div className="row">
                    <div className="col-md-8 m-auto">
                        <h1 className="display-4 text-center">Log In</h1>
                        <p className="lead text-center">Sign in to your Devel'd-In account</p>
                        <form onSubmit={this.onSubmit} >
                            <div className="form-group">
                                <input type="email" className="form-control form-control-lg" placeholder="Email Address" name="email" value={this.onSubmit.email} onSubmit={this.onSubmit}/>
                            </div>
                            <div className="form-group">
                                  <input type="password" className="form-control form-control-lg" placeholder="Password" name="password" value={this.onSubmit.password} onChange={this.onChange}/>
                            </div>
                                    <input type="submit" className="btn btn-info btn-block mt-4" />
                        </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        )//end return
    }//end render
}//end class Login
export default Login;
