import React, { Component } from 'react'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import { Link } from 'react-router-dom';
import { deletePost, addLike, removeLike } from '../../actions/postActions';

class PostItem extends Component {
  onUnlikeClick(id){
    this.props.removeLike(id);
  }//end onUnlikeClick

  onLikeClick(id){
    this.props.addLike(id);
  }//end onLikeClick

  onDeleteClick(id){
    this.props.deletePost(id);
  }//end onDeleteClick

  addLike(id){
    this.props.addLike(id);
  }//end addLike

  removeLike(id){
    this.props.removeLike(id);
  }//end removeLike

  findUserLikes(likes){
    const { auth } = this.props;
   const like = (likes.filter(like => like.user === auth.user.id).length > 0) ? true : false
   return like

  }//end findUserLikes

  render() {
    const  { post, auth, showActions } = this.props;

    return (

          <div className="card card-body mb-3">
              <div className="row">
                <div className="col-md-2">
                  <a href="profile.html">
                    <img 
                    className="rounded-circle d-none d-md-block"
                    src={post.avatar}
                    alt=""/>
                  </a>
                  <br />
                  <p className="text-center">{post.name }</p>
                </div>
                <div className="col-md-10">
                    <p className="lead">{post.text}</p>
                    {!showActions?
                      (<span>
                      <button onClick={this.onLikeClick.bind(this, post._id)} type="button" className="btn btn-light mr-1">
                      <i className={ classnames("fas fa-thumbs-up", {
                        'text-success': this.findUserLikes(post.likes),
                        'text-info': !this.findUserLikes(post.likes)
                      })}/>
                      <span className="badge badge-light">{post.likes.length}</span>
                    </button>

                    <button onClick={this.onUnlikeClick.bind(this, post._id)} type="button" className="btn btn-light mr-1">
                    <i className={ classnames("fas fa-thumbs-down", {
                        'text-info': !this.findUserLikes(post.likes),
                        'text-danger': this.findUserLikes(post.likes)
                      })}/>
                      </button>

                    <Link to={`/post/${post._id}`}
                    className="btn btn-info mr-1">
                      Comments
                    </Link>
                    {post.user === auth.user.id                    
                    ?(<button onClick={this.onDeleteClick
                      .bind(this, post._id)} type="button" className="btn btn-danger mr-1">
                      <i className="fas fa-times"/></button>)
                    :(null)}</span>)
                    :null}
                  </div>
              </div>
            </div>
    )
  }
}
PostItem.defaultProps = {
  showActions: true
}//end default props

PostItem.propTypes = {
  removeLike: PropTypes.func.isRequired,
  addLike: PropTypes.func.isRequired,
  deletePost: PropTypes.func.isRequired,
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
  auth: state.auth
})
export default connect(mapStateToProps, {deletePost, addLike, removeLike})(PostItem)
