import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import  isEmpaty  from '../../validation/is-empty'
class ProfileItem extends Component {
    render (){
        const {profile} = this.props;
        return (
            <div className="card cadr-body bg-light mb-3">
                <div className="row">
                    <div className="col-2">
                        <img src={profile.user.avatar} alt="No Image" className="rounded-circle" />
                    </div>

                    <div className="col-lg-6 col-md-4 col-8">
                        <h3>{profile.user.name}</h3>
                            <p>
                                {profile.status} {isEmpaty(profile.company) ? null: (<span>at {profile.company}</span>)}
                            </p>
                            <p>
                            {isEmpaty(profile.location)?null:(<span>at {profile.location}</span>)}
                            </p>
                            <Link to={`/profile/${profile.handle}`} className="btn btn-info">View Profile</Link>
                            <div className="col-md-4.d-none d-md-block">
                                <h4>Skill Set</h4>
                                <ul className="list-group">
                                    {profile.skills.slice(0,4).map((skill, index) => (
                                        <li key={index} className="list-group-item">
                                            <i className="fa fa-check pr-1" />
                                            {skill}
                                        </li>
                                    ))}
                                   </ul>
                            </div>
                    </div>
                </div>
            </div>
        )
    }
}

ProfileItem.propTypes = {
    profile : PropTypes.object.isRequired
}
export default ProfileItem;
