import axios from 'axios';
import { ADD_POST, GET_ERRORS } from './types'
import { clearErrors } from '../../../../Downloads/MERN Stack Front To Back Full Stack React, Redux & Node.js/[Tutsgalaxy.com] - MERN Stack Front To Back Full Stack React, Redux & Node.js/12 Posts  Comments/devconnector_S12/client/src/actions/postActions';
//ADD POST
export const addPost = postData => dispatch => {
    dispatch(clearErrors());
    axios
    .post('/api/posts', postData)
    .then(res => {
        dispatch({
            type: ADD_POST,
            payload: res.data
        })//end dispatch
    }).catch(err => dispatch({
        type: GET_ERRORS,
        payload: err.response.data
    }))//end catch
}//end addPost
